package loops;

public class i1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * if(condition1)
		 * {
		 * cond1 true
		 * }
		 * 
		 * else if(cond2)
		 * {
		 * cond2 true
		 * }
		 * 
		 * else
		 * {
		 * false
		}*/
		
		int m=3;
		int n=20;
		int o=30;
		
		if(m>n && m>o)
		{
			System.out.println("m is bigger");
		}
		
		else if(n>m && n>o)
		{
			System.out.println("n is bigger");
		}
		else
		{
			System.out.println(" o is bigger");
		}
		
		
	}

}
